INSERT INTO `content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_shortDescription`, `field_year`, `field_tools`, `field_client`, `field_clientUrl`, `field_responsibilities`, `field_heading`, `field_introduction`, `field_ogTitle`, `field_ogDescription`, `field_briefHeading`, `field_briefBody`, `field_learnedHeading`, `field_learnedBody`, `field_analysisHeading`, `field_analysisBody`, `field_dictionary`, `field_social`, `field_quotation`, `field_genre`, `field_projectUrl`, `field_color`, `field_loop`, `field_videoWidth`, `field_videoHeight`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('12', '12', 'en_us', 'Composer Hsin-Lei Chen Portfolio', NULL, 'An online listening experience and artist\'s archive for Hsin-Lei Chen, a Taiwanese composer who is known for her experimental folk music.', '2015', '[\"flash\",\"css3\",\"html5\",\"javascript\",\"sketch\",\"webgl\"]', 'Hsin-Lei Chen', 'http://hsinlei.com', 'Visual Design', 'Composer\r\nHsin-Lei Chen\r\nPortfolio', NULL, 'Composer Hsin-Lei Chen Portfolio', 'This is the OG description.', 'Project Brief', '<p>Hsin Lei is a excellent classic music composor from Taiwan, graduate form Cincinnati School of Music 10 years ago.</p><p>In order to better understand Hsin Lei\'s music, we met in Beijing after getting her the first version of design. Hsin Lei inspired by Asian culture and transform into western style melody. This great conversation we had given me some new perspective to Hsin Lei and her music and the more I understand about her life, the more we can understand her music.</p>', 'What I\'ve Learned', '<p>A good design for a musical portfolio is not just playing some lovely tunes. It expresses the deepest feelings and emotions of the composer. Following with this direction, a multi-media form of experience can resonate with the audience.<br></p>', 'Project Analysis', '<p>This is the project analysis text.</p>', NULL, NULL, NULL, 'Web Application', '', 'blue', '0', '0', '0', '2015-05-06 02:06:58', '2015-06-05 03:55:53', '75ff3a9c-f4da-4a67-8051-e0efe173d286');
