	this._scrollButton = goog.dom.query( '.intro .scroll', el )[ 0 ];

	this._modules = [];
	this._stickies = [];
	this._lazyLoaders = [];

	this._animateInTweener = null;
	this._animateOutTweener = null;
	this._autoScrollTweener = null;

