goog.inherits( gux.controllers.Intro, goog.events.EventTarget );
goog.addSingletonGetter( gux.controllers.Intro );


gux.controllers.Intro.prototype.activate = function() {

	this.resize();

	this._eventHandler.listen( window, goog.events.EventType.RESIZE, this.resize, false, this );
	this._eventHandler.listen( this._loader, gux.events.EventType.ANIMATE_COMPLETE, this.onLoadAnimateComplete, false, this );

	TweenMax.ticker.addEventListener( 'tick', this.update, this );

	// animate in
	var timeline = new TimelineMax( {
		'delay': 0.5,
		'onComplete': this.onTypeTransitionComplete,
		'onCompleteScope': this
	} );

	var startY = this._two.height * 1.5;
	var endY = 0;
	var tweeners = [];
	goog.object.forEach( this._shapeConfig, function( config ) {
		var tweener = TweenMax.fromTo( config, 1.5, {
			slideY: startY,
			scale: 0
		}, {
			slideY: endY,
			scale: 1,
			'ease': Strong.easeOut
		} );

		tweeners.push( tweener );
	} );
	timeline.add( tweeners, '+=0', 'start', .05 );

	//
	goog.Timer.callOnce( function() {
		goog.dom.classlist.enable( this.el, 'animate-in-ui', true );
	}, 1400, this );

	goog.Timer.callOnce( function() {
		goog.dom.classlist.enable( this.el, 'animate-in-loader', true );
	}, 2500, this );

	//
	gux.mainScroller.lock( 0 );
};


gux.controllers.Intro.prototype.deactivate = function() {

	this._eventHandler.removeAll();

	TweenMax.ticker.removeEventListener( 'tick', this.update, this );

	gux.mainScroller.unlock();
};


gux.controllers.Intro.prototype.disposeInternal = function() {

	goog.base( this, 'disposeInternal' );

	this.deactivate();

	this._loader.dispose();
};


gux.controllers.Intro.prototype.createPolygon = function( vector, hex, opt_opacity, opt_stroke ) {

	var flattenedVectors = [];

	var anchors = goog.array.forEach( vector, function( v ) {
		flattenedVectors.push( v[ 0 ] * 10, v[ 1 ] * 10 );
	} );

	var polygon = this._two.makePath.apply( this._two, flattenedVectors );
	polygon.opacity = goog.isNumber( opt_opacity ) ? opt_opacity : 1;

	if ( opt_stroke ) {
		polygon.linewidth = .25;
		polygon.stroke = hex;
		polygon.noFill();
	} else {
		polygon.fill = hex;
		polygon.noStroke();
	}

	return polygon;
};


gux.controllers.Intro.prototype.updateMask = function() {

	var halfWidth = this._two.width / 2;
	var halfHeight = this._two.height / 2;

	var maskY = goog.math.lerp( -halfHeight, halfHeight, this._maskRatio );
	this._mask.translation.set( halfWidth, maskY );
};


gux.controllers.Intro.prototype.update = function() {

	var config = this._shapeConfig;

	this._shapeG.translation.set( config.g.baseX, config.g.baseY + config.g.slideY );
	this._shadowG.translation.set( config.g.baseX + config.g.shadowOffset, config.g.baseY + config.g.shadowOffset + config.g.slideY );
	this._strokeG.translation.copy( this._shapeG.translation );

	this._shapeG.scale = this._shadowG.scale = this._strokeG.scale = this._shapeBaseScale * config.g.scale;

	this._shapeU.translation.set( config.u.baseX, config.u.baseY + config.u.slideY );
	this._shadowU.translation.set( config.u.baseX + config.u.shadowOffset, config.u.baseY + config.u.shadowOffset + config.u.slideY );
	this._strokeU.translation.copy( this._shapeU.translation );

	this._shapeU.scale = this._shadowU.scale = this._strokeU.scale = this._shapeBaseScale * config.u.scale;

	this._shapeXTop.translation.set( config.xTop.baseX, config.xTop.baseY + config.xTop.slideY );
	this._shadowXTop.translation.set( config.xTop.baseX + config.xTop.shadowOffset, config.xTop.baseY + config.xTop.shadowOffset + config.xTop.slideY );
	this._strokeXTop.translation.copy( this._shapeXTop.translation );

	this._shapeXTop.scale = this._shadowXTop.scale = this._strokeXTop.scale = this._shapeBaseScale * config.xTop.scale;

	this._shapeXBottom.translation.set( config.xBottom.baseX, config.xBottom.baseY + config.xBottom.slideY );
	this._shadowXBottom.translation.set( config.xBottom.baseX + config.xBottom.shadowOffset, config.xBottom.baseY + config.xBottom.shadowOffset + config.xBottom.slideY );
	this._strokeXBottom.translation.copy( this._shapeXBottom.translation );

	this._shapeXBottom.scale = this._shadowXBottom.scale = this._strokeXBottom.scale = this._shapeBaseScale * config.xBottom.scale;

	this._openingQGroup.scale = this._shapeBaseScale / 2.25;
	this._closingQGroup.scale = this._shapeBaseScale / 2.25;

	this._openingQGroup.translation.set( config.openingQ.baseX, config.openingQ.slideY );
	this._closingQGroup.translation.set( config.closingQ.baseX, config.closingQ.slideY );

	//
	this._two.update();
};


gux.controllers.Intro.prototype.resize = function() {

	var canvasSize = goog.style.getSize( this._canvasContainer );

	var resWidth, resHeight;

	if ( this._maxResolution.aspectRatio() > canvasSize.aspectRatio() ) {
		resWidth = Math.min( this._maxResolution.width, canvasSize.width );
		resHeight = resWidth / canvasSize.aspectRatio();
	} else {
		resHeight = Math.min( this._maxResolution.height, canvasSize.height );
		resWidth = resHeight * canvasSize.aspectRatio();
	}

	var scale = Math.min( Math.max( 260, resHeight * .4 ), resWidth * .3 );
	this._shapeBaseScale = scale / 10;

	this._two.width = canvasSize.width;
	this._two.height = canvasSize.height;
	this._two.update();

	// resize bg
	this._whiteBg.scale = Math.max( this._two.width, this._two.height );
	this._whiteBg.translation.set( this._two.width / 2, this._two.height / 2 );

	//
	var margin = Math.max( 6, scale * .06 );
	var startX = ( this._two.width - ( scale / 2 + margin + scale + margin + scale / 2 ) ) / 2;
	var startY = this._two.height * .52;

	var config = this._shapeConfig;
	config.g.baseX = startX;
	config.g.baseY = startY;
	config.g.shadowOffset = scale * .1;

	config.u.baseX = startX + scale + margin;
	config.u.baseY = startY;
	config.u.shadowOffset = scale * .08;

	config.xTop.baseX = startX + scale * 2 + margin * 2;
	config.xTop.baseY = startY - scale / 4;
	config.xTop.shadowOffset = scale * .05;

	config.xBottom.baseX = startX + scale * 2 + margin * 2;
	config.xBottom.baseY = startY + scale / 4;
	config.xBottom.shadowOffset = scale * .05;

	config.openingQ.baseX = this._two.width * .07;
	config.closingQ.baseX = this._two.width * .8;

	// test mask
	var halfWidth = canvasSize.width / 2;
	var halfHeight = canvasSize.height / 2;

	goog.array.forEach( this._mask.vertices, function( v, i ) {
		switch ( i ) {
			case 0:
				v.x = -halfWidth;
				v.y = -halfHeight;
				break;

			case 1:
				v.x = halfWidth;
				v.y = -halfHeight;
				break;

			case 2:
				v.x = halfWidth;
				v.y = halfHeight;
				break;

			case 3:
				v.x = -halfWidth;
				v.y = halfHeight;
				break;
		}
	} );

	this.updateMask();

	//
	this.update();
};


gux.controllers.Intro.prototype.onTypeTransitionComplete = function() {

	goog.dom.classlist.addRemove( this._bottomBar, 'hide', 'show' );

	this._loader.load();
};


gux.controllers.Intro.prototype.onLoadAnimateComplete = function( e ) {

	console.log( "LOAD ANIMATE COMPLETE!", e.assets );

	// animate mask
	TweenMax.to( this, 1, {
		_maskRatio: 0,
		'ease': Quad.easeInOut,
		'onUpdate': this.updateMask,
		'onUpdateScope': this
	} );

	// animate indicator
	goog.dom.classlist.addRemove( this._bottomBar, 'show', 'hide' );

	// animate quotation
	goog.dom.classlist.enable( this._quotation, 'animate-in', true );

	var config = this._shapeConfig;

	TweenMax.fromTo( config.openingQ, 2, {
		slideY: this._two.height + this._shapeBaseScale
	}, {
		slideY: this._two.height * .3,
		'delay': 0.7,
		'ease': Expo.easeOut
	} );

	TweenMax.fromTo( config.closingQ, 2, {
		slideY: this._two.height + this._shapeBaseScale
	}, {
		slideY: this._two.height * .45,
		'delay': 0.9,
		'ease': Expo.easeOut
	} );

	// animate out
	TweenMax.to( this.el, 1, {
		'y': '-100%',
		'display': 'none',
		'delay': 4,
		'ease': Strong.easeInOut,
		'onComplete': function() {
			this.dispatchEvent( gux.events.EventType.ANIMATE_OUT_COMPLETE );
			this.dispose();
		},
		'onCompleteScope': this
	} );

	var mainContent = goog.dom.getElement( 'main-content' );
	TweenMax.fromTo( mainContent, 1, {
		'opacity': 0,
		'y': 150
	}, {
		'opacity': 1,
		'y': 0,
		'delay': 4,
		'ease': Strong.easeInOut,
		'clearProps': 'opacity, y'
	} );
};
goog.provide( 'gux.fx.DummyScroller' );

goog.require( 'goog.async.Delay' );
goog.require( 'goog.events.EventTarget' );
goog.require( 'goog.events.EventHandler' );
goog.require( 'goog.dom' );
goog.require( 'goog.fx.Dragger' );
goog.require( 'goog.math.Size' );
goog.require( 'goog.math.Box' );
goog.require( 'goog.userAgent' );
goog.require( 'gux.events' );

/**
 * @constructor
 */
gux.fx.DummyScroller = function( viewOuter, scrollbar, opt_speed, opt_ease ) {

	goog.base( this );

	// scroll properties
	this._dummyScrollTop = 0;
	this._viewInnerY = 0;
	this._viewInnerHeight = 0;
	this._viewOuterHeight = 0;
	this._dummyInnerHeight = 0;
	this._dummyOuterHeight = 0;
	this._handleHeight = 0;
	this._scrollbarHeight = 0;
	this._speed = opt_speed || 1;
	this._ease = opt_ease || .25;

	this._callbacks = {};
	this._callbacks[ gux.events.EventType.SCROLL_UPDATE ] = [];
	this._callbacks[ gux.events.EventType.SCROLL_COMPLETE ] = [];

	this._isAnimating = false;

	// dom elements
	this._viewOuter = viewOuter;

	this._dummyOuter = goog.dom.createDom( 'div', {
		'className': 'dummy-scroller'
	} );
	this._dummyInner = goog.dom.createDom( 'div', {
		'className': 'inner'
	} );

	goog.style.setStyle( this._dummyOuter, {
		'position': 'absolute',
		'top': 0,
		'left': 0,
		'width': goog.userAgent.MOBILE ? '100%' : 'calc(100% + 50px)',
		'height': '100%',
		'overflow': 'auto',
		'z-index': 100
	} );

	goog.dom.classlist.add( this._viewOuter, 'dummy-scroll' );

	goog.dom.appendChild( this._dummyOuter, this._dummyInner );
	goog.dom.appendChild( goog.dom.getParentElement( this._viewOuter ), this._dummyOuter );

	this._scrollbar = scrollbar;
	this._handle = goog.dom.getElementByClass( 'handle', this._scrollbar );

	// draggable
	this._draggable = new Draggable( this._viewOuter, {
		'type': 'scrollTop',
		'zIndexBoost': false,
		'cursor': 'inherit',
		'throwProps': true,
		'onDrag': this.onThrowUpdate,
		'onDragScope': this,
		'onThrowUpdate': this.onThrowUpdate,
		'onThrowUpdateScope': this
	} );
	this._draggable.enabled( goog.userAgent.MOBILE );

	// events
	this._eventHandler = new goog.events.EventHandler( this );
	this._disableDummyScrollDelay = new goog.async.Delay( this.disableDummyScroll, 250, this );

	this._dragger = new goog.fx.Dragger( this._handle );
	this._dragger.defaultAction = goog.nullFunction;
	this._draggerLimits = new goog.math.Rect( 0, 0, 0, 0 );

	// init
	this.disableDummyScroll();
	this.unlock();
	this.resize();
};
goog.inherits( gux.fx.DummyScroller, goog.events.EventTarget );


gux.fx.DummyScroller.prototype.activate = function() {

	this._eventHandler.listen( window, goog.events.EventType.RESIZE, this.resize, false, this );

	this._eventHandler.listen( this._dragger, goog.fx.Dragger.EventType.START, this.onDragHandleStart, false, this );
	this._eventHandler.listen( this._dragger, goog.fx.Dragger.EventType.END, this.onDragHandleEnd, false, this );
	this._eventHandler.listen( this._dragger, goog.fx.Dragger.EventType.DRAG, this.onDragHandle, false, this );
};


gux.fx.DummyScroller.prototype.deactivate = function() {

	this._eventHandler.removeAll();
};


gux.fx.DummyScroller.prototype.reset = function() {

	this.resize();

	this._dummyOuter.scrollTop = 0;
	this.scrollTo( 0 );
};


gux.fx.DummyScroller.prototype.lock = function( opt_y ) {

	if ( !goog.userAgent.MOBILE ) {

		this._eventHandler.unlisten( this._dummyOuter, goog.events.EventType.SCROLL, this.onDummyScroll, false, this );
		this._eventHandler.unlisten( this._viewOuter, 'mousewheel', this.onMouseWheel, false, this );
	}

	goog.style.setStyle( this._scrollbar, 'visibility', 'hidden' );
	goog.style.setStyle( this._dummyOuter, 'overflow', 'hidden' );

	if ( goog.isNumber( opt_y ) ) {
		this.scrollTo( opt_y );
	}

	this._draggable.enabled( false );
};


gux.fx.DummyScroller.prototype.unlock = function() {

	if ( !goog.userAgent.MOBILE ) {

		this._eventHandler.listen( this._dummyOuter, goog.events.EventType.SCROLL, this.onDummyScroll, false, this );
		this._eventHandler.listen( this._viewOuter, 'mousewheel', this.onMouseWheel, false, this );
	}

	goog.style.setStyle( this._scrollbar, 'visibility', 'visible' );
	goog.style.setStyle( this._dummyOuter, 'overflow', 'auto' );

	this._draggable.enabled( goog.userAgent.MOBILE ? true : false );
};


gux.fx.DummyScroller.prototype.getProgress = function() {

	return Math.abs( this._viewInnerY / ( this._viewInnerHeight - this._viewOuterHeight ) );
};


gux.fx.DummyScroller.prototype.getScrollTop = function() {

	return this._viewInnerY;
};


gux.fx.DummyScroller.prototype.enableDummyScroll = function() {

	goog.style.setStyle( this._dummyOuter, 'visibility', 'visible' );
};


gux.fx.DummyScroller.prototype.disableDummyScroll = function() {

	goog.style.setStyle( this._dummyOuter, 'visibility', 'hidden' );
};


gux.fx.DummyScroller.prototype.showScrollbar = function() {

	goog.style.setElementShown( this._scrollbar, true );
};


gux.fx.DummyScroller.prototype.hideScrollbar = function() {

	goog.style.setElementShown( this._scrollbar, false );
};


gux.fx.DummyScroller.prototype.addCallback = function( type, callback ) {

	goog.array.insert( this._callbacks[ type ], callback );
};


gux.fx.DummyScroller.prototype.removeCallback = function( type, callback ) {

	goog.array.remove( this._callbacks[ type ], callback );
};


gux.fx.DummyScroller.prototype.scrollTo = function( y, syncDummyScroll ) {

	this._viewInnerY = Math.round( y );

	this._viewOuter.scrollTop = this._viewInnerY;

	var handleRatio = Math.abs( this._viewInnerY / this._viewInnerHeight );
	var handleY = Math.round( this._scrollbarHeight * handleRatio );
	goog.style.setStyle( this._handle, 'top', handleY + 'px' );

	if ( syncDummyScroll ) {

		this._dummyScrollRatio = goog.math.clamp(
			this._viewInnerY / ( this._viewInnerHeight - this._viewOuterHeight ), 0, 1 );

		this._dummyOuter.scrollTop = ( this._dummyInnerHeight - this._dummyOuterHeight ) * this._dummyScrollRatio;
	}

	this.dispatchUpdateCallbacks();
};


gux.fx.DummyScroller.prototype.resize = function() {

	this._viewOuterHeight = goog.style.getSize( this._viewOuter ).height;
	this._viewInnerHeight = this._viewOuter.scrollHeight;

	this._dummyOuterHeight = goog.style.getSize( this._dummyOuter ).height;
	this._dummyInnerHeight = this._viewInnerHeight * ( 1 / this._speed );
	goog.style.setHeight( this._dummyInner, this._dummyInnerHeight );

	// resize scrollbar
	var viewInnerHeightFraction = this._viewInnerHeight / this._viewOuterHeight;

	this._scrollbarHeight = goog.style.getSize( this._scrollbar ).height;

	this._handleHeight = this._scrollbarHeight / viewInnerHeightFraction;
	goog.style.setHeight( this._handle, this._handleHeight );

	this._draggerLimits.height = this._scrollbarHeight - this._handleHeight;
	this._dragger.setLimits( this._draggerLimits );
};


gux.fx.DummyScroller.prototype.dispatchUpdateCallbacks = function() {

	var updateCallbacks = this._callbacks[ gux.events.EventType.SCROLL_UPDATE ];
	var i, l = updateCallbacks.length;
	var progress = this.getProgress();

	for ( i = 0; i < l; i++ ) {
		updateCallbacks[ i ]( progress, this._viewInnerY );
	}
};


gux.fx.DummyScroller.prototype.dispatchCompleteCallbacks = function() {

	var completeCallbacks = this._callbacks[ gux.events.EventType.SCROLL_COMPLETE ];
	var i, l = completeCallbacks.length;
	var progress = this.getProgress();

	for ( i = 0; i < l; i++ ) {
		completeCallbacks[ i ]( progress, this._viewInnerY );
	}
};


gux.fx.DummyScroller.prototype.onDummyScroll = function( e ) {

	this._dummyScrollRatio = goog.math.clamp(
		this._dummyOuter.scrollTop / ( this._dummyInnerHeight - this._dummyOuterHeight ), 0, 1 );

	if ( !this._isAnimating ) {

		this._isAnimating = true;
		goog.fx.anim.registerAnimation( this );
	}
};


gux.fx.DummyScroller.prototype.onThrowUpdate = function() {

	this._viewInnerY = -this._draggable.y;

	this.dispatchUpdateCallbacks();
};


gux.fx.DummyScroller.prototype.onThrowComplete = function() {

	this._viewInnerY = -this._draggable.y;

	this.dispatchCompleteCallbacks();
};


gux.fx.DummyScroller.prototype.onMouseWheel = function( e ) {

	this.enableDummyScroll();

	this._disableDummyScrollDelay.start();
};


gux.fx.DummyScroller.prototype.onDragHandleStart = function( e ) {

	goog.dom.classlist.enable( this._scrollbar, 'dragging', true );
};


gux.fx.DummyScroller.prototype.onDragHandleEnd = function( e ) {

	goog.dom.classlist.enable( this._scrollbar, 'dragging', false );
};


gux.fx.DummyScroller.prototype.onDragHandle = function( e ) {

	var ratio = goog.math.clamp( e.dragger.deltaY / this._draggerLimits.height, 0, 1 );

	this._dummyOuter.scrollTop = ( this._dummyInnerHeight - this._dummyOuterHeight ) * ratio;
};


gux.fx.DummyScroller.prototype.onAnimationFrame = function( now ) {

	var startY = 0;
	var endY = this._viewInnerHeight - this._viewOuterHeight;

	var targetY = goog.math.lerp( startY, endY, this._dummyScrollRatio );

	this._viewInnerY += ( targetY - this._viewInnerY ) * this._ease;

	if ( goog.math.nearlyEquals( this._viewInnerY, targetY, 2 ) ) {

		this._viewInnerY = targetY;

		goog.fx.anim.unregisterAnimation( this );

		this._isAnimating = false;

		// call complete callbacks
		this.dispatchCompleteCallbacks();
	}

	this.scrollTo( this._viewInnerY );
};
